import { movies } from "../movies";

const initialState = {
  movies: movies,
  favMovies: [],
  sira: 0,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    /* case ADD_MOVIE:
      return {
        ...state,
        movies: [...state.movies, action.payload],
      };
    case DELETE_MOVIE:
      return {
        ...state,
        movies: state.movies.filter((item) => action.payload !== item.id),
      };
      */
    default:
      return state;
  }
};

export default reducer;
